export default class Option {
    flags: string;
    defaultValue: any;
    required: boolean;
    optional: boolean;
    no: boolean;
    short: string;
    long: string;
    description: string;
    constructor(flags: string, description?: string, defaultValue?: any);
    name(): string;
    is(arg: string): boolean;
    parse(opts: any, args: string[]): string[];
}
